mapper.select:
select m.member_id as memberId , m2.name as name , p.product_main_image as mainImageUrl , p.product_price as price , c.product_quantity as quantity , p.product_title as title , p.product_delivery_charge as deliveryCharge , c.shopping_cart_id as cartId , p.product_id as productId
from shopping_cart as c
join member m on c.member_id = m.member_id join product p on c.product_id = p.product_id join member m2 on p.member_id = m2.member_id where c.member_id = #{memberId}

mapper.update:
update member as b, (select point from member where member_id = ${memberId} ) as a, product as p
set b.point = a.point - ${usedPoint}, p.product_quantity = p.product_quantity - ${quantity}
where p.product_id = ${productId}
  and b.member_id = ${memberId}

update member
set point = point + ${addPoint}
where member_id = ${memberId}
