mapper.select:
select q.question_id as questionId, question_contents as questionContents, secret_question as secretQuestion, question_date as questionDate, question_state as questionState, question_category_name as questionCategoryName, q.product_id as productId, m1.account_id as userAccountId, answer_id as answerId, answer_contents as answerContents, m2.account_id as sellerAccountId, answer_date as answerDate
from question as q inner join question_category as qc on q.question_category_id = qc.question_category_id inner join member as m1 on q.member_id = m1.member_id left outer join answer as a on q.question_id = a.question_id left outer join member as m2 on a.member_id = m2.member_id where q.product_id = ${productId} order by questionId desc
limit #{firstRecordIndex}, #{criteria.recordsPerPage}

select count(*)
from question
where product_id = ${productId}

mapper.insert:
insert into question(question_contents,
                     question_state,
                     question_category_id,
                     product_id,
                     member_id


                , secret_question


)values( #{questionContents},
         '답변대기',
         ${questionCategoryId},
         ${productId},
         ${memberId}


               , #{secretQuestion}


         )
